import { useNavigate } from "react-router-dom";
import React, { useEffect, useState } from "react";
import PageWithIdentification from "./PageWithIdentification";
import authenticationApi from "../../utils/Authentication";

function Login({ onLogin, setLoggedIn }) {
  const navigate = useNavigate();
  const [data, setData] = useState({
    password: "",
    email: ""
  });
  const [userEmail, setUserEmail] = useState('');

  function handleChange(e) {
    const {name, value} = e.target;
    setData(prevState => ({...prevState, [name]: value}));
  }

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      const result = await authenticationApi.pushLogin(data);
      if (result.token) {
        authenticationApi.setToken(result.token);
        const userData = await authenticationApi.pullDataAuth();
        setUserEmail(userData.data.email);
        onLogin(userData.data.email);
        setLoggedIn(true);
        navigate('/main');
      }
    } catch (error) {
      console.error(`Ошибка при отправки данных регистрации пользователя: ${error}`);
    }
  }

  useEffect(() => {
    const token = localStorage.getItem('jwt');
    if(token) {
      authenticationApi.setToken(token);
      authenticationApi.pullDataAuth()
      .then(userData => {
        setUserEmail(userData.data.email);
        onLogin(userData.data.email);
        setLoggedIn(true);
      })
      .catch(error => console.error(`Ошибка при загрузке данных пользователя: ${error}`));
      setLoggedIn(false);
    } else {
      setLoggedIn(false);
    }
}, [onLogin, setLoggedIn]);

  return (
    <PageWithIdentification
      name="Вход"
      textSubmit="Войти"
      signatute=""
      linkText=""
      linkTo="#"
      onSubmit={handleSubmit}
      onChange={handleChange}
    />
  )
}

export default Login;
