import { useEffect, useState } from "react";
import api from "../utils/Api";

function Main({onEditProfile, onAddPlace, onEditAvatar}) {

	const [isUserName, setUserName] = useState('');
	const [isUserDescription, setUserDescription] = useState('');
	const [UserAvatar, setUserAvatar] = useState('');
	const [cards, setCards] = useState(['']);

	useEffect(() => {
		const getUserInfo = async () => {
			try {
				const [dataProfile, dataCards] = await Promise.all([
					api.pullProfileInfo(),
					api.pullCardInfo(),
				]);
				setUserName(dataProfile.name);
				setUserDescription(dataProfile.about);
				setUserAvatar(dataProfile.avatar);
				setCards(dataCards);
			} catch (error) {
				console.error(`Ошибка при загрузке данных пользователя: ${error}`);
			}
		};
		
		getUserInfo();
	}, []);

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__info">
          <div 
						className="profile__avatar-container cursor-pointer">
            <div
							style={{ backgroundImage: `url(${UserAvatar})` }}
              className="profile__avatar"
            ></div>
            <div
              className="profile__avatar-overlay"
              onClick={onEditAvatar}
            ></div>
          </div>
          <div className="profile__data">
            <h1 className="profile__name">{isUserName}</h1>
            <button
              className="profile__button-edit button-clickable cursor-pointer"
              aria-label="Редактировать"
              type="button"
              onClick={onEditProfile}
            ></button>
            <p className="profile__activity">{isUserDescription}</p>
          </div>
        </div>
        <button
          className="profile__button-add button-clickable cursor-pointer"
          aria-label="Добавить"
          type="button"
          onClick={onAddPlace}
        ></button>
      </section>
      <section className="places">
        <ul className="places__cards">

          {cards.map((card) => (
            <li className="cards" key={card._id}>
              <button
                className="cards__trash cursor-pointer button-clickable"
                aria-label="Мусорка"
                type="button"
              ></button>
              <div style={{ backgroundImage: `url(${card.link})` }} className="cards__img cursor-pointer"></div>
              <div className="cards__name-form">
                <h2 className="cards__name">{card.name}</h2>
                <div className="cards__heart-box">
                  <button
                    className="cards__heart cursor-pointer"
                    aria-label="Сердце"
                    type="button"
                  ></button>
                  <p className="cards__heart-count">{card.likes.length}</p>
                </div>
              </div>
            </li>
					))}


        </ul>
      </section>
    </main>
  );
}

export default Main;
