export default function handleSubmit(request, popupInstance, loadingText = "Сохранение...") {
  popupInstance.renderLoading(true, loadingText);
  request()
    .then(() => {
      popupInstance.close()
    })
    .catch((err) => {
      console.error(`Ошибка в ${popupInstance}: ${err}`);
    })
    .finally(() => {
      popupInstance.renderLoading(false);
    });
}
