import { Link, useLocation, useNavigate } from 'react-router-dom';
import logo from "../../images/Logo.svg";

function Header({ email, onSignOut }) {
  const location = useLocation();
  const navigate = useNavigate();

  function signOut() {
    onSignOut();
    navigate('/sign-in')
  }

  return (
    <header className="header">
      <ul className="header__links">
        <li>
          <Link to="/main">
            <img
              src={logo}
              alt="Логотип"
              className="logo button-clickable"
              id="logo"
            />
          </Link>
        </li>
        <li>
        {email ? (
          <div className='header__link-container'>
            <p className="header__email">{email}</p>
            <button onClick={signOut} className="header__button cursor-pointer button-clickable">Выйти</button>
          </div>
        ) : (
          location.pathname === '/sign-in' ? (
            <Link to="/sign-up" className="header__link button-clickable">
              Регистрация
            </Link>
          ) : (
            <Link to="/sign-in" className="header__link button-clickable">
              Войти
            </Link>
          )
        )}
        </li>
      </ul>
    </header>
  );
}

export default Header;
