import { useHistory } from "react-router-dom";
import React, { useState } from "react";
import PageWithIdentification from "./PageWithIdentification";
import authenticationApi from "../../utils/Authentication";

function Register() {
  const history = useHistory();
  const [data, setData] = useState({
    password: "",
    email: ""
  });

  function handleChange(e) {
    const {name, value} = e.target;
    setData(prevState => ({...prevState, [name]: value}));
  }

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      const result = await authenticationApi.pushRegistration(data);
      if (result.data) {
        history.push('/sign-in');
      }
    } catch (error) {
      console.error("Ошибка при отправки данных регистрации пользователя:", error);
    }
  }

  return (
    <PageWithIdentification
      name="Регистрация"
      textSubmit="Зарегистрироваться"
      signatute="Уже зарегистрированы? "
      linkText="Войти"
      linkTo="/sign-in"
      onSubmit={handleSubmit}
      onChange={handleChange}
    />
  )
}

export default Register;
